    public static int maximalSquare(char[][] matrix) {
        int maxLen = 0;
        for (int i = 0; i < matrix.length - maxLen; i++) {
            for (int j = 0; j < matrix[0].length - maxLen; j++) {
                if (matrix[i][j] == '1') {
                    if (i == 12 && j == 8) {
                        System.out.println("...");
                    }
                    int length = getLength(matrix, i, j);
                    if (length > maxLen) {
                        maxLen = length;
                        System.out.println(i + " " + j);
                    }
                }
            }
        }
        return maxLen * maxLen;
    }

    private static int getLength(char[][] matrix, int i, int j) {
        int length = 1;
        int endi = i + 1;
        int endj = j + 1;
        while (endi < matrix.length && endj < matrix[0].length) {
            if (endj == 15) {
                System.out.println("....");
            }
            for (int k = j; k <= endj; k++) {
                if (matrix[endi][k] != '1') {
                    return length;
                }
            }
            for (int k = i; k <= endi; k++) {
                if (matrix[k][endj] != '1') {
                    return length;
                }
            }
            length++;
            endi++;
            endj++;
        }
        return length;
    }
